import ctypes import os
DLL_PATH = os.path.join(os.path.dirname(__file__), "src", "hRng.dll")
hrng = ctypes.WinDLL(DLL_PATH)
# Function prototypes hrng.MaxRNG.argtypes = [ctypes.c_void_p, ctypes.c_int] hrng.MaxRNG.restype = ctypes.c_int
hrng.RNG_SelfTest.restype = ctypes.c_int
def test_maxrng(): buf = (ctypes.c_ubyte * 32)() result = hrng.MaxRNG(buf, 32) print("MaxRNG result:", result) print("Random bytes:", bytes(buf).hex())
def test_selftest(): result = hrng.RNG_SelfTest() print("RNG_SelfTest result:", result)
